INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2013-09-06 10:40:20', '2013-09-06 10:40:20', 'Bienvenue dans WordPress. Ceci est votre premier article. Modifiez-le ou supprimez-le, puis lancez-vous !', 'Bonjour tout le monde&nbsp;!', '', 'publish', 'closed', 'closed', '', 'bonjour-tout-le-monde', '', '', '2013-09-10 20:19:24', '2013-09-10 19:19:24', '', 0, 'http://localhost/ffdesigner/?p=1', 0, 'post', '', 0),
(2, 1, '2013-09-06 10:40:20', '2013-09-06 10:40:20', 'Voici un exemple de page. Elle est différente d&rsquo;un article de blog, en cela qu''elle restera à la même place, et s''affichera dans le menu de navigation de votre site (en fonction de votre thème). La plupart des gens commencent par écrire une page « À Propos » qui les présente aux visiteurs potentiels du site. Vous pourriez y écrire quelque chose de ce tenant :\n\n<blockquote>Bonjour ! Je suis un mécanicien qui aspire à devenir un acteur, et voici mon blog. J''habite à Bordeaux, j''ai un super chien baptisé Russell, et j''aime la vodka-ananas (ainsi que regarder la pluie tomber).</blockquote>\n\n...ou bien quelque chose comme ça :\n\n<blockquote>La société 123 Machin Truc a été créée en 1971, et n''a cessé de proposer au public des machins-trucs de qualité depuis lors. Située à Saint-Remy-en-Bouzemont-Saint-Genest-et-Isson, 123 Machin Truc emploie 2 000 personnes, et fabrique toutes sortes de bidules super pour la communauté bouzemontoise.</blockquote>\n\nÉtant donné que vous êtes un nouvel utilisateur de WordPress, vous devriez vous rendre sur votre <a href="http://localhost/ffdesigner/wp-admin/">tableau de bord</a> pour effacer la présente page, et créer de nouvelles pages avec votre propre contenu. Amusez-vous bien&nbsp;!', 'Page d&rsquo;exemple', '', 'publish', 'open', 'open', '', 'page-d-exemple', '', '', '2013-09-06 10:40:20', '2013-09-06 10:40:20', '', 0, 'http://localhost/ffdesigner/?page_id=2', 0, 'page', '', 0),
(41, 1, '2013-09-09 10:52:27', '0000-00-00 00:00:00', 'This is my post.', 'My post', '', 'pending', 'closed', 'open', '', '', '', '', '2013-09-09 10:52:27', '0000-00-00 00:00:00', '', 0, 'http://localhost/ffdesigner/?p=41', 0, 'post', '', 0),
(42, 1, '2013-09-09 10:52:28', '2013-09-09 09:52:28', '', 'galaxy-s4', '', 'inherit', 'closed', 'open', '', 'galaxy-s4', '', '', '2013-09-09 10:52:28', '2013-09-09 09:52:28', '', 0, 'http://localhost/ffdesigner/wp-content/uploads/2013/09/galaxy-s41.jpg', 0, 'attachment', 'image/jpeg', 0),
